import './onClick.css';
import Footer from '../Components/Footer';
import Header from '../Components/Header';
import witches from '../images/witches.png';
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const OnClick = () => {
  const [movieDetails, setMovieDetails] = useState(null);
  const { movieId } = useParams();
  const [selectedMovie, setSelectedMovie] = useState(null);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        const response = await fetch(`https://api.themoviedb.org/3/movie/${movieId}?api_key=f211287ee7b15b080bb278734cd356db`);
        if (!response.ok) {
          throw new Error(`Failed to fetch movie details (HTTP status: ${response.status})`);
        }
        const data = await response.json();
        setMovieDetails(data);
      } catch (error) {
        console.error('Error fetching movie details:', error.message);
      }
    };

    fetchMovieDetails();
  }, [movieId]);

  const handleContinueClick = () => {
    setSelectedMovie(movieId);
    window.location.href = `/confirmation/${movieId}`;
  };


  function handleTimeSelection(selectedTime) {
    console.log("Selected time:", selectedTime);
    // You can add your logic here to perform any action based on the selected time
}

// Example usage
document.querySelectorAll('.time11, .time2').forEach(item => {
    item.addEventListener('click', event => {
        const selectedTime = event.target.innerText;
        handleTimeSelection(selectedTime);
    });
});


  return (
    <div className='onclick'>
      <Header />
      <link
        rel='stylesheet'
        href='https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0-beta2/css/all.min.css'
        integrity='sha512-YWzhKL2whUzgiheMoBFwW8CKV4qpHQAEuvilg9FAn5VJUDwKZZxkJNuGM4XkWuk94WCrrwslk8yWNGmY1EduTA=='
        crossOrigin='anonymous'
        referrerPolicy='no-referrer'
      />
      <script
        src='https://kit.fontawesome.com/2d95ff6af1.js'
        crossOrigin='anonymous'
      ></script>
      <div className="cover"></div>
      <div className="info">
        <div className="title">
          {movieDetails && (
            <div>
              <h2>{movieDetails.title}</h2>
              <i className="fa-regular fa-clock"></i>
              <span>1HR 45MIN</span>
            </div>
          )}
        </div>
        <div className="des">
        {movieDetails && (
            <div>
              <h2>{movieDetails.overview}</h2>
            </div>
          )}
        </div>
      </div>

      <div className="progress-bar">
        <div className="first">
          <span>1</span>
          <p>Select Movie</p>
        </div>
        <div className="second">
          <span>2</span>
          <p>Confirmation</p>
        </div>
        <div className="third">
          <span>3</span>
          <p>Reserve / Buy</p>
        </div>
      </div>

      <div className="movie-info">
        <div className="movie-cover">
        {movieDetails && (
    <img src={`https://image.tmdb.org/t/p/w500${movieDetails.poster_path}`} alt={movieDetails.title} />
  )}
        </div>
        <div className="movie-info-title">
        {movieDetails && (
            <div>
              <h2>{movieDetails.title}</h2>
              <i className="fa-regular fa-clock"></i>
              <span>1HR 45MIN</span>
            </div>
          )}
          <div className="genre">
            <i className="fa-solid fa-film"></i>
            <span>Fantasy / Sci-fi</span>
          </div>
          <span>IMAX 2D</span>
          <div className="time1">
            <div className="time11">
              <span>13:00</span>
            </div>
            <div className="time2">
              <span>20:00</span>
            </div>
          </div>
        </div>
        <div className="proceed">
          <button onClick={handleContinueClick}>continue</button>
        </div>
      </div>
      <Footer />
    </div>
  );
}

export default OnClick;
